import React, { useState, useEffect } from 'react';
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
} from '@mui/material';
import axios from '../api/axiosConfig';  // Update this path as necessary

const roles = ['Admin', 'Technician', 'Tenant'];

const EditUserModal = ({ open, handleClose, user, fetchUsers }) => {
  const [formData, setFormData] = useState({
    username: '',
    role: '',
    firstName: '',
    lastName: '',
  });

  useEffect(() => {
    if (user) {
      setFormData({
        username: user.username || '',
        role: user.role || '',
        firstName: user.firstName || '',
        lastName: user.lastName || '',
      });
    }
  }, [user]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

const handleSubmit = async () => {
  try {
    const response = await axios.put(`/api/users/${user._id}`, {
      firstName: formData.firstName,
      lastName: formData.lastName,
      role: formData.role,
      username: formData.username,
    });

    if (response.status === 200) {  // Ensure the response status is checked for success
      alert('User updated successfully!');
      handleClose();  // Close the modal on success
    } else {
      // If the response is not 200, handle it as an error
      alert('Failed to update user. Please try again.');
    }
  } catch (error) {
    console.error('Error updating user:', error);
    alert('Failed to update user. Please try again.');
  }
};


  return (
    <Dialog open={open} onClose={handleClose} fullWidth maxWidth="sm">
      <DialogTitle>Edit User Information</DialogTitle>
      <DialogContent>
        {/* First Name */}
        <TextField
          label="First Name"
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        {/* Last Name */}
        <TextField
          label="Last Name"
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        {/* Username */}
        <TextField
          label="Username"
          name="username"
          value={formData.username}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        {/* Role */}
        <FormControl fullWidth margin="normal">
          <InputLabel>Role</InputLabel>
          <Select
            name="role"
            value={formData.role}
            onChange={handleChange}
            fullWidth
          >
            {roles.map((role) => (
              <MenuItem key={role} value={role}>
                {role}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="secondary">
          Cancel
        </Button>
        <Button onClick={handleSubmit} color="primary">
          Save
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default EditUserModal;
