import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#bb86fc', // Custom dark theme primary color
    },
    secondary: {
      main: '#03dac6', // Custom secondary color for dark theme
    },
    background: {
      default: '#121212', // Dark background
      paper: '#1d1d1d',   // Surface background color
    },
    text: {
      primary: '#ffffff', // White text for primary content
      secondary: '#b0bec5', // Gray text for secondary content
    },
    error: {
      main: '#cf6679', // Error color in dark theme
    },
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif', // Use the Roboto font
    button: {
      textTransform: 'none', // Disable uppercase for buttons
    },
  },
});

export default theme; // Ensure theme is exported as default
