import React, { useState, useEffect } from 'react';
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Grid,
  Tabs,
  Tab,
  Box,
  Typography,
  IconButton,
  Snackbar,
  Tooltip,
  Chip,
  Card,
  CardContent,
  CardActions,
  Divider
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import CloseIcon from '@mui/icons-material/Close';
import useMediaQuery from '@mui/material/useMediaQuery';
import { useTheme } from '@mui/material/styles';
import axios from '../api/axiosConfig';
import ReactQuill from 'react-quill'; // Rich text editor w/ formatting tools for Work Order Notes
import 'react-quill/dist/quill.snow.css'; // Import Quill styles

const statusMapping = {
  new: 'New',
  pending: 'Pending',
  delayed: 'Delayed',
  closed: 'Closed',
  excluded: 'Excluded',
  're-opened': 'Re-Opened',
};

const taskTypes = [
  'Clean Up / Spill',
  'Cooling Issue',
  'Electrical Issue',
  'Equipment Repairs',
  'Fire Safety',
  'General Maintenance Request',
  'Health and Safety',
  'Heating Issues',
  'Lighting Issues',
  'Mechanical Issues',
  'Painting / Touch Ups',
  'Pest Control',
  'Plumbing Issues',
  'Waste Issues',
];

const EditWorkOrderModal = ({ open, handleClose, workOrder, fetchWorkOrders }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm')); // Determine if it's mobile or not

  const [formData, setFormData] = useState({
    status: '',
    taskType: '',
    roomNumber: '',
    description: '',
    notes: '',
    submittedBy: '',
    notesHistory: [],
    newNote: '',
    createdDate: '',
    attachments: [],
  });

  const [originalFormData, setOriginalFormData] = useState(null);
  const [selectedTab, setSelectedTab] = useState(0);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [tooltipText, setTooltipText] = useState("Copy Task ID");
  const [tooltipOpen, setTooltipOpen] = useState(false); // Control tooltip state
  const [copied, setCopied] = useState(false); // State to track if the chip was clicked
  const [selectedFiles, setSelectedFiles] = useState([]); // State for file selection

  useEffect(() => {
    if (workOrder) {
      const initialData = {
        _id: workOrder._id || '',
        taskType: workOrder.taskType || '',
        roomNumber: workOrder.roomNumber || '',
        description: workOrder.details || '',
        notesHistory: workOrder.notesHistory || [],
        createdDate: new Date(workOrder.createdDate).toLocaleString(),
        attachments: workOrder.attachments || [],
        submittedBy: workOrder.submittedBy
          ? `${workOrder.submittedBy.firstName} ${workOrder.submittedBy.lastName}`
          : 'N/A',
        newNote: '',
      };

      setFormData(initialData);
      setOriginalFormData(initialData);
    }
  }, [workOrder]);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleAddNote = async () => {
    if (formData.newNote.trim() === '') {
      return;
    }

    const newNote = {
      note: formData.newNote,
      username: "Your User", // Add relevant user details, e.g., current logged-in user
      timestamp: new Date().toISOString(),
    };

    // Optimistically update the state before waiting for the server response
    setFormData((prevState) => ({
      ...prevState,
      notesHistory: [
        ...prevState.notesHistory,
        newNote, // Add the new note immediately
      ],
      newNote: '', // Clear the new note input field
    }));

    try {
      // Send the new note to the server
      const response = await axios.put(`/api/work-orders/${workOrder._id}/add-note`, newNote);

      // Replace the notesHistory with the latest data from the server, if necessary
      setFormData((prevState) => ({
        ...prevState,
        notesHistory: response.data.notesHistory,
      }));
    } catch (error) {
      alert('Failed to add the note. Please try again.');

      // Optionally: Rollback the UI change if the request fails
      setFormData((prevState) => ({
        ...prevState,
        notesHistory: prevState.notesHistory.filter((note) => note !== newNote), // Remove the optimistic note
      }));
    }
  };


  const handleDeleteNote = async (index) => {
    const updatedNotesHistory = formData.notesHistory.filter((_, i) => i !== index);

    try {
      await axios.put(`/api/work-orders/${workOrder._id}`, {
        notesHistory: updatedNotesHistory,
      });

      setFormData({
        ...formData,
        notesHistory: updatedNotesHistory,
      });
    } catch (error) {
      alert('Failed to delete the note. Please try again.');
    }
  };

  const handleDownloadAttachment = (attachmentPath) => {
    const url = `${process.env.REACT_APP_API_URL}/${attachmentPath}`;
    window.open(url, '_blank');
  };

  const handleDeleteAttachment = async (filename) => {
    try {
      const response = await axios.delete(`/api/work-orders/${workOrder._id}/attachments/${filename}`);
      setFormData({
        ...formData,
        attachments: response.data.attachments,
      });
    } catch (error) {
      alert('Failed to delete attachment.');
    }
  };

  const handleFileChange = (e) => {
    setSelectedFiles(e.target.files);
  };

  const handleUploadAttachments = async () => {
    if (!workOrder._id) {
      alert('Work order ID is missing');
      return;
    }
    if (selectedFiles.length === 0) {
      alert('Please select at least one file.');
      return;
    }

    const uploadData = new FormData();
    for (let i = 0; i < selectedFiles.length; i++) {
      uploadData.append('files', selectedFiles[i]);
    }

    try {
      const response = await axios.post(`/api/work-orders/${workOrder._id}/attachments`, uploadData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      setFormData({
        ...formData,
        attachments: response.data.attachments,
      });

      alert('Attachments uploaded successfully!');
    } catch (error) {
      alert('Failed to upload attachments. Please try again.');
    }
  };

  const handleSubmit = async () => {
    if (!workOrder || !workOrder._id) {
      return;
    }

    try {
      const updatedData = {
        status: formData.status,
        taskType: formData.taskType,
        roomNumber: formData.roomNumber,
        details: formData.description,
        priority: formData.priority,
        notesHistory: formData.notesHistory,
      };

      await axios.put(`/api/work-orders/${workOrder._id}`, updatedData);

      setSnackbarOpen(true);
      fetchWorkOrders();
      handleClose();
    } catch (error) {
      alert('Failed to update the work order. Please try again.');
    }
  };

  const handleUndo = async () => {
    try {
      if (!originalFormData || !originalFormData._id) {
        return;
      }

      setFormData(originalFormData);

      await axios.put(`/api/work-orders/${originalFormData._id}`, originalFormData);

      fetchWorkOrders();
      setSnackbarOpen(false);
    } catch (error) {
      console.error('Failed to undo changes.', error);
    }
  };

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  // Function to handle copying the Task ID
  const handleCopyTaskId = async () => {
    if (workOrder?.workOrderId) {
      try {
        await navigator.clipboard.writeText(workOrder.workOrderId);
        setTooltipText("Copied!");
        setTooltipOpen(true);
        setCopied(true);  // Set copied state to true
        setTimeout(() => {
          setTooltipOpen(false);
          setCopied(false);  // Reset copied state after tooltip disappears
        }, 1500);
      } catch (error) {
        setTooltipText("Failed to copy");
        setTooltipOpen(true);
        setTimeout(() => setTooltipOpen(false), 1500);
      }
    }
  };

  // Function to handle mouse enter and reset the tooltip to "Copy Task ID"
  const handleMouseEnter = () => {
    if (!copied) {
      setTooltipText("Copy Task ID");
      setTooltipOpen(true); // Open tooltip on hover
    }
  };

  return (
    <>
<style jsx>{`
  .ql-toolbar {
    background-color: #2e2e2e; /* Dark background for the toolbar */
    border: 1px solid #444; /* Darker border */
  }

  /* Ensure all toolbar elements are styled properly */
  .ql-toolbar button,
  .ql-toolbar .ql-picker,
  .ql-toolbar .ql-picker-item,
  .ql-toolbar .ql-picker-label {
    color: #ffffff !important; /* Set all buttons and pickers to white */
  }

  /* Specifically target SVG icons inside toolbar buttons */
  .ql-toolbar button svg,
  .ql-toolbar .ql-picker svg,
  .ql-toolbar .ql-formats button svg {
    fill: #ffffff !important; /* White color for all SVG icons */
  }

  /* Hover effects for all buttons, pickers, and SVG icons */
  .ql-toolbar button:hover,
  .ql-toolbar .ql-picker:hover,
  .ql-toolbar .ql-picker-item:hover,
  .ql-toolbar .ql-picker-label:hover,
  .ql-toolbar .ql-formats button:hover svg {
    color: #ff4081 !important; /* Set hover color to pink */
    fill: #ff4081 !important;
  }

  /* Target the editor content area */
  .ql-container {
    background-color: #121212; /* Dark background for the editor */
    color: #ffffff !important; /* White text in the editor */
  }

  /* Specifically style the active state for buttons and pickers */
  .ql-toolbar button.ql-active,
  .ql-toolbar .ql-picker-item.ql-selected,
  .ql-toolbar button.ql-active svg {
    color: #ffffff !important;
    fill: #ffffff !important;
  }
`}</style>
      <Dialog open={open} onClose={handleClose} fullWidth maxWidth="md" fullScreen={isMobile}>
        <DialogTitle>
          Edit Work Order
          {workOrder?.workOrderId && (
            <Tooltip
              title={tooltipText}
              arrow
              open={tooltipOpen} // Tooltip only shows when clicked or hovered
              onClose={() => setTooltipOpen(false)}
              disableHoverListener={isMobile || copied} // Disable hover when copied is true
              placement="top"
            >
              <Chip
                label={`Task ID: ${workOrder.workOrderId}`}
                onClick={handleCopyTaskId}
                onMouseEnter={handleMouseEnter} // Handle mouse enter to reset tooltip text
                onMouseLeave={() => !isMobile && setTooltipOpen(false)} // Only close on hover out if not copied
                onTouchEnd={handleCopyTaskId} // For mobile, tap works without hover
                sx={{ float: 'right', cursor: 'pointer', fontWeight: 'bold', color: 'primary.main' }}
              />
            </Tooltip>
          )}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>Update the work order details below:</DialogContentText>

          <Tabs
            value={selectedTab}
            onChange={handleTabChange}
            variant="scrollable"
            scrollButtons={false}
          >
            <Tab label="Work Order Details" />
            <Tab label="Notes and Updates" />
            <Tab label="Attachments" />
          </Tabs>

          {selectedTab === 0 && (
            <Box>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <FormControl fullWidth margin="normal" variant="outlined">
                    <InputLabel>Status</InputLabel>
                    <Select
                      name="status"
                      value={formData.status}
                      onChange={handleChange}
                      label="Status"
                    >
                      {Object.keys(statusMapping).map((status) => (
                        <MenuItem key={status} value={status}>
                          {statusMapping[status]}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  <FormControl fullWidth margin="normal" variant="outlined">
                    <InputLabel>Task Type</InputLabel>
                    <Select
                      name="taskType"
                      value={formData.taskType}
                      onChange={handleChange}
                      label="Task Type"
                    >
                      {taskTypes.map((type) => (
                        <MenuItem key={type} value={type}>
                          {type}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  <TextField
                    label="Room Number"
                    name="roomNumber"
                    value={formData.roomNumber}
                    onChange={handleChange}
                    fullWidth
                    margin="normal"
                    variant="outlined"
                  />

                  <TextField
                    label="Submitted By"
                    name="submittedBy"
                    value={formData.submittedBy || 'N/A'}
                    disabled
                    fullWidth
                    margin="normal"
                    variant="outlined"
                  />

                  <TextField
                    label="Date Created"
                    name="createdDate"
                    value={formData.createdDate}
                    fullWidth
                    margin="normal"
                    disabled
                    variant="outlined"
                  />
                </Grid>

                <Grid item xs={12} sm={6}>
                  <TextField
                    label="Original Description"
                    name="description"
                    value={formData.description}
                    fullWidth
                    multiline
                    rows={isMobile ? 3 : 6}
                    margin="normal"
                    disabled
                  />
                </Grid>
              </Grid>
            </Box>
          )}

{selectedTab === 1 && (
  <Box>
    <Typography variant="h6" gutterBottom>
      Add new note
    </Typography>
    <ReactQuill
    value={formData.newNote}
    onChange={(value) => setFormData({ ...formData, newNote: value })} // Update newNote with formatted content
    //theme="snow" Omit quill theme for custom stuff below
    placeholder="Add new note"
    modules={{
      toolbar: [
        [{ 'header': '1'}, {'header': '2'}, { 'font': [] }],
        [{size: []}],
        ['bold', 'italic', 'underline', 'strike', 'blockquote'],
        [{'list': 'ordered'}, {'list': 'bullet'}],
        ['link', 'image'],
        ['clean']                                         // Remove formatting button
      ],
    }}
  />
    <Button variant="contained" color="primary" onClick={handleAddNote}>
      Add Note
    </Button>

    <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
  Notes History
</Typography>
<Box sx={{ mt: 2 }}>
  {formData.notesHistory.slice().reverse().map((note, index) => (
    <Card 
      key={index} 
      sx={{ 
        mb: 2, 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        bgcolor: '#262626', // Background color
        border: '1px solid', 
        borderColor: '#878787', // Border color
        boxShadow: 6, // Elevation of the card
      }}
    >
      <CardContent sx={{ flexGrow: 1 }}>
        <Typography variant="subtitle2" color="#FFBC40">
          <strong>{note.username}</strong> at {new Date(note.timestamp).toLocaleString()}
        </Typography>
        <Divider sx={{ my: 1 }} />
        {/* Here we render the formatted content */}
        <Typography 
          variant="body1" 
          sx={{ whiteSpace: 'pre-wrap', color: 'text.primary' }}
          dangerouslySetInnerHTML={{ __html: note.note }} // Render HTML content
        />
      </CardContent>
      <CardActions>
        <IconButton size="small" onClick={() => handleDeleteNote(index)} sx={{ ml: 1 }}>
          <DeleteIcon fontSize="small" />
        </IconButton>
      </CardActions>
    </Card>
  ))}
</Box>




  </Box>
)}


          {selectedTab === 2 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Existing Attachments
              </Typography>
              {formData.attachments.length > 0 ? (
                <Box>
                  {formData.attachments.map((attachment, index) => (
                    <Box key={index} sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                      <Typography>
                        <button
                          onClick={() => handleDownloadAttachment(attachment)}
                          style={{ background: 'none', border: 'none', padding: 0, color: 'blue', textDecoration: 'underline', cursor: 'pointer' }}
                        >
                          {attachment.split('/').pop()}
                        </button>
                      </Typography>
                      <IconButton onClick={() => handleDeleteAttachment(attachment.split('/').pop())}>
                        <DeleteIcon />
                      </IconButton>
                    </Box>
                  ))}
                </Box>
              ) : (
                <Typography>No attachments available.</Typography>
              )}

              <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
                Upload New Attachments
              </Typography>
              <input type="file" multiple onChange={handleFileChange} />
              <Button variant="contained" color="primary" onClick={handleUploadAttachments}>
                Upload Attachments
              </Button>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleSubmit} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={7500}
        onClose={handleCloseSnackbar}
        message="Work order updated"
        action={
          <>
            <Button color="inherit" size="small" onClick={handleUndo}>
              Undo
            </Button>
            <IconButton
              size="small"
              aria-label="close"
              color="inherit"
              onClick={handleCloseSnackbar}
            >
              <CloseIcon />
            </IconButton>
          </>
        }
      />
    </>
  );
};

export default EditWorkOrderModal;
