import React, { useState, useEffect } from 'react';
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Grid,
  Tabs,
  Tab,
  Box,
  Typography,
  IconButton,
  Snackbar
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import CloseIcon from '@mui/icons-material/Close';
import axios from '../api/axiosConfig';

const statusMapping = {
  new: 'New',
  pending: 'Pending',
  delayed: 'Delayed',
  closed: 'Closed',
  excluded: 'Excluded',
  're-opened': 'Re-Opened',
};

const taskTypes = [
  'Clean Up / Spill',
  'Cooling Issue',
  'Electrical Issue',
  'Equipment Repairs',
  'Fire Safety',
  'General Maintenance Request',
  'Health and Safety',
  'Heating Issues',
  'Lighting Issues',
  'Mechanical Issues',
  'Painting / Touch Ups',
  'Pest Control',
  'Plumbing Issues',
  'Waste Issues',
];

const EditWorkOrderModal = ({ open, handleClose, workOrder, fetchWorkOrders }) => {
  const [formData, setFormData] = useState({
    status: '',
    taskType: '',
    roomNumber: '',
    description: '',
    notes: '',
    submittedBy: '',
    notesHistory: [],
    newNote: '',
    createdDate: '', // Add createdDate to formData
    attachments: [], // Add attachments array to handle attachments
  });

  const [originalFormData, setOriginalFormData] = useState(null); // Store original form data for undo
  const [selectedTab, setSelectedTab] = useState(0);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [undoing, setUndoing] = useState(false);  // To track if undo action is in progress
  const [selectedFiles, setSelectedFiles] = useState([]); // To handle file uploads

  useEffect(() => {
    if (workOrder) {
      const initialData = {
        _id: workOrder._id || '',  // Ensure _id is included
        status: workOrder.status || '',
        taskType: workOrder.taskType || '',
        roomNumber: workOrder.roomNumber || '',
        description: workOrder.details || '',
        notesHistory: workOrder.notesHistory || [],
        createdDate: new Date(workOrder.createdDate).toLocaleString(), // Add createdDate and format it
        attachments: workOrder.attachments || [], // Load existing attachments
        newNote: '',
      };

      setFormData(initialData);
      setOriginalFormData(initialData);  // Store the original form data with the ID
    }
  }, [workOrder]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  const handleFileChange = (e) => {
    setSelectedFiles(e.target.files);
  };

  const handleUploadAttachments = async () => {
    console.log("Uploading for work order ID:", workOrder._id); // Add this log statement
    if (!workOrder._id) {
      alert('Work order ID is missing');
    return;
  }
    if (selectedFiles.length === 0) {
      alert('Please select at least one file.');
      return;
    }

    const formData = new FormData();
    for (let i = 0; i < selectedFiles.length; i++) {
      formData.append('files', selectedFiles[i]);
    }

    try {
      const response = await axios.post(`/api/work-orders/${workOrder._id}/attachments`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      // Update the attachments in the formData
      setFormData({
        ...formData,
        attachments: response.data.attachments,
      });

      alert('Attachments uploaded successfully!');
    } catch (error) {
      alert('Failed to upload attachments. Please try again.');
    }
  };

  const handleDownloadAttachment = (attachmentPath) => {
  // Assuming the attachment path is relative to the 'uploads' folder
  const url = `http://localhost:5151/${attachmentPath}`;
  window.open(url, '_blank');
};


  // Add a new note using /add-note endpoint
  const handleAddNote = async () => {
    if (formData.newNote.trim() === '') {
      return; // Prevent adding empty notes
    }

    try {
      const newNote = {
        note: formData.newNote,
      };

      const response = await axios.put(`/api/work-orders/${workOrder._id}/add-note`, newNote);

      setFormData({
        ...formData,
        notesHistory: response.data.notesHistory,
        newNote: '',
      });

    } catch (error) {
      alert('Failed to add the note. Please try again.');
    }
  };

  // Delete a note from the notes history
  const handleDeleteNote = async (index) => {
    const updatedNotesHistory = formData.notesHistory.filter((_, i) => i !== index);

    try {
      await axios.put(`/api/work-orders/${workOrder._id}`, {
        notesHistory: updatedNotesHistory
      });

      setFormData({
        ...formData,
        notesHistory: updatedNotesHistory,
      });

    } catch (error) {
      alert('Failed to delete the note. Please try again.');
    }
  };

  // Save the work order and close the modal
  const handleSubmit = async () => {
    if (!workOrder || !workOrder._id) {
      return;  // Exit early if there's no valid work order or ID
    }

    try {
      // Save the work order with updated information
      const updatedData = {
        status: formData.status,
        taskType: formData.taskType,
        roomNumber: formData.roomNumber,
        details: formData.description, // Ensure 'details' is updated, not 'description'
        priority: formData.priority,
        notesHistory: formData.notesHistory,
      };

      await axios.put(`/api/work-orders/${workOrder._id}`, updatedData);

      setSnackbarOpen(true);
      fetchWorkOrders();
      handleClose(); // Close the modal after saving

    } catch (error) {
      alert('Failed to update the work order. Please try again.');
    }
  };

  // Undo action to revert to original data
  const handleUndo = async () => {
    try {
      if (!originalFormData || !originalFormData._id) {
        return;  // Exit early if there's no valid work order or ID
      }

      setUndoing(true);

      // Restore original form data locally
      setFormData(originalFormData);

      // Restore original form data on the backend
      await axios.put(`/api/work-orders/${originalFormData._id}`, originalFormData);

      fetchWorkOrders();  // Re-fetch work orders after undoing changes
      setSnackbarOpen(false);  // Close the Snackbar after undo
      setUndoing(false);  // Reset the undoing state

    } catch (error) {
      setUndoing(false);  // Reset the undoing state in case of an error
    }
  };

  // Handle closing of the Snackbar
  const handleCloseSnackbar = (event, reason) => {
    if (reason === 'clickaway' || undoing) {
      return; // Ignore clicks outside the snackbar or during undo
    }
    setSnackbarOpen(false); // Allow manual close
  };

  return (
    <>
      <Dialog open={open} onClose={handleClose} fullWidth maxWidth="md">
        <DialogTitle>Edit Work Order</DialogTitle>
        <DialogContent>
          <DialogContentText>Update the work order details below:</DialogContentText>

          {/* Tabs for switching between Details, Notes, and Attachments */}
          <Tabs value={selectedTab} onChange={handleTabChange}>
            <Tab label="Work Order Details" />
            <Tab label="Notes and Updates" />
            <Tab label="Attachments" /> {/* New Attachments tab */}
          </Tabs>

          {/* Work Order Details Tab */}
          {selectedTab === 0 && (
            <Box>
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <FormControl fullWidth margin="normal">
                    <InputLabel>Status</InputLabel>
                    <Select
                      name="status"
                      value={formData.status}
                      onChange={handleChange}
                      fullWidth
                    >
                      {Object.keys(statusMapping).map((status) => (
                        <MenuItem key={status} value={status}>
                          {statusMapping[status]}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  <FormControl fullWidth margin="normal">
                    <InputLabel>Task Type</InputLabel>
                    <Select
                      name="taskType"
                      value={formData.taskType}
                      onChange={handleChange}
                      fullWidth
                    >
                      {taskTypes.map((type) => (
                        <MenuItem key={type} value={type}>
                          {type}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  <TextField
                    label="Room Number"
                    name="roomNumber"
                    value={formData.roomNumber}
                    onChange={handleChange}
                    fullWidth
                    margin="normal"
                  />

                  <TextField
                    label="Submitted By"
                    name="submittedBy"
                    value={formData.submittedBy?.username || 'N/A'}
                    disabled
                    fullWidth
                    margin="normal"
                  />

                  <TextField
                    label="Date Created"
                    name="createdDate"
                    value={formData.createdDate}
                    fullWidth
                    margin="normal"
                    disabled
                  />
                </Grid>

                <Grid item xs={6}>
                  <TextField
                    label="Original Description"
                    name="description"
                    value={formData.description}
                    fullWidth
                    multiline
                    rows={6}
                    margin="normal"
                    disabled
                  />
                </Grid>
              </Grid>
            </Box>
          )}

          {/* Notes Tab */}
          {selectedTab === 1 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Add new note
              </Typography>
              <TextField
                label="Add new note"
                name="newNote"
                value={formData.newNote}
                onChange={handleChange}
                fullWidth
                multiline
                rows={3}
                margin="normal"
              />
              <Button variant="contained" color="primary" onClick={handleAddNote}>
                Add Note
              </Button>

              <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
                Notes History
              </Typography>
              <Box sx={{ mt: 2 }}>
                {formData.notesHistory.map((note, index) => (
                  <Box key={index} sx={{ mb: 2, borderBottom: '1px solid #ccc', pb: 1 }}>
                    <Typography variant="body2">
                      <strong>{note.username}</strong> at{' '}
                      {new Date(note.timestamp).toLocaleString()}:
                    </Typography>
                    <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap' }}>
                      {note.note}
                    </Typography>
                    <IconButton size="small" onClick={() => handleDeleteNote(index)}>
                      <DeleteIcon fontSize="small" />
                    </IconButton>
                  </Box>
                ))}
              </Box>
            </Box>
          )}

          {/* Attachments Tab */}
          {selectedTab === 2 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Existing Attachments
              </Typography>
              {formData.attachments.length > 0 ? (
                <Box>
                  {formData.attachments.map((attachment, index) => (
                    <Box key={index} sx={{ mb: 2 }}>
                      <Typography>
                        <button onClick={() => handleDownloadAttachment(attachment)} style={{ background: 'none', border: 'none', padding: 0, color: 'blue', textDecoration: 'underline', cursor: 'pointer' }}>
  {attachment.split('/').pop()}
</button>

                      </Typography>
                    </Box>
                  ))}
                </Box>
              ) : (
                <Typography>No attachments available.</Typography>
              )}

              <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
                Upload New Attachments
              </Typography>
              <input type="file" multiple onChange={handleFileChange} />
              <Button variant="contained" color="primary" onClick={handleUploadAttachments}>
                Upload Attachments
              </Button>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleSubmit} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for showing success message with Undo option and an "X" to close */}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={7500}
        onClose={handleCloseSnackbar}
        message="Work order updated"
        action={
          <>
            <Button color="inherit" size="small" onClick={handleUndo}>
              Undo
            </Button>
            <IconButton
              size="small"
              aria-label="close"
              color="inherit"
              onClick={handleCloseSnackbar}
            >
              <CloseIcon />
            </IconButton>
          </>
        }
      />
    </>
  );
};

export default EditWorkOrderModal;
