import React, { useEffect, useState } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  TablePagination,
  TableSortLabel,
  Checkbox,
  Toolbar,
  IconButton,
  Tooltip,
  Box,
  Fab,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add'; // Import the Add icon
import { alpha } from '@mui/material/styles';
import axios from '../api/axiosConfig';
import WorkOrderFormModal from './WorkOrderFormModal';
import EditWorkOrderModal from './EditWorkOrderModal';

const WorkOrderManagement = () => {
  const [workOrders, setWorkOrders] = useState([]);
  const [openModal, setOpenModal] = useState(false); // State to control modal visibility
  const [openEditModal, setEditModalOpen] = useState(false); // Add state for edit modal
  const [selectedWorkOrder, setSelectedWorkOrder] = useState(null); // Track the work order to edit
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [order, setOrder] = useState('asc');
  const [orderBy, setOrderBy] = useState('taskType');
  const [selected, setSelected] = useState([]); // Use _id for row selection

  // Fetch the work orders
  useEffect(() => {
    fetchWorkOrders();
  }, []);

  const fetchWorkOrders = () => {
    axios
      .get('/api/work-orders')
      .then((response) => setWorkOrders(response.data))
      .catch((error) => console.error(error));
  };


  const handleDoubleClick = (workOrder) => {
    setSelectedWorkOrder(workOrder);
    setEditModalOpen(true); // Open the edit modal on double-click
  };

  const handleCloseEditModal = () => {
    setEditModalOpen(false);
    setSelectedWorkOrder(null); // Clear the selected work order after closing
  };

  // Handle modal open and close
  const handleOpenModal = () => setOpenModal(true); // Open modal
  const handleCloseModal = () => setOpenModal(false); // Close modal

  // Sorting function
  const handleRequestSort = (property) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

const sortComparator = (a, b) => {
  if (!a[orderBy] || !b[orderBy]) return 0; // Prevent error if values are null

  if (orderBy === 'workOrderId') {
    // Handle numeric comparison for workOrderId
    return order === 'asc' ? a[orderBy] - b[orderBy] : b[orderBy] - a[orderBy];
  }

  if (orderBy === 'createdDate') {
    // Handle date comparison for createdDate
    return order === 'asc'
      ? new Date(a[orderBy]) - new Date(b[orderBy])
      : new Date(b[orderBy]) - new Date(a[orderBy]);
  }

  // Handle string comparison for other fields
  return order === 'asc'
    ? a[orderBy].localeCompare(b[orderBy])
    : b[orderBy].localeCompare(a[orderBy]);
};


  // Handle page change
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  // Handle rows per page change
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Handle row selection
  const handleSelectAllClick = (event) => {
    if (event.target.checked) {
      const newSelecteds = workOrders.map((n) => n._id); // Select by _id
      setSelected(newSelecteds);
      return;
    }
    setSelected([]);
  };

  const handleCheckboxClick = (event, _id) => {
    const selectedIndex = selected.indexOf(_id);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, _id);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1)
      );
    }

    setSelected(newSelected);
  };

  const isSelected = (_id) => selected.indexOf(_id) !== -1;

  // Bulk delete selected work orders
  const handleDeleteSelected = () => {
    if (selected.length === 0) {
      console.error('No work orders selected for deletion');
      return;
    }

    // Deleting each selected work order based on the _id
    const promises = selected.map((_id) =>
      axios.delete(`/api/work-orders/${_id}`) // Use _id for deletion
        .then(() => {
          console.log(`Deleted work order with _id: ${_id}`);
        })
        .catch(error => {
          console.error(`Failed to delete work order with _id: ${_id}`, error.response ? error.response.data : error.message);
        })
    );

    // After deleting, refetch the work orders
    Promise.all(promises)
      .then(() => {
        fetchWorkOrders();
        setSelected([]); // Clear selected rows after deletion
      })
      .catch((error) => console.error('Failed to complete deletion of work orders', error));
  };

  const paginatedWorkOrders = workOrders
    .sort(sortComparator)
    .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage);

  return (
    <Box sx={{ width: '100%' }}>
      <Paper sx={{ width: '100%', mb: 2 }}>
        {/* Toolbar for bulk actions */}
        <Toolbar
          sx={[
            { pl: { sm: 2 }, pr: { xs: 1, sm: 1 } },
            selected.length > 0 && {
              bgcolor: (theme) =>
                alpha(theme.palette.primary.main, theme.palette.action.activatedOpacity),
            },
          ]}
        >
          {selected.length > 0 ? (
            <Typography
              sx={{ flex: '1 1 100%' }}
              color="inherit"
              variant="subtitle1"
              component="div"
            >
              {selected.length} selected
            </Typography>
          ) : (
            <>
              <Typography
                sx={{ flex: '1 1 auto' }}
                variant="h6"
                id="tableTitle"
                component="div"
              >
                Work Orders
              </Typography>

              {/* Circular "+" button next to the title */}
              <Fab
                color="primary"
                aria-label="add"
                onClick={handleOpenModal} // Opens the modal for creating a new work order
                sx={{ ml: 2 }}
              >
                <AddIcon />
              </Fab>
            </>
          )}

          {selected.length > 0 ? (
            <Tooltip title="Delete">
              <IconButton onClick={handleDeleteSelected}>
                <DeleteIcon />
              </IconButton>
            </Tooltip>
          ) : (
            <></>
          )}
        </Toolbar>

        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell padding="checkbox">
                  <Checkbox
                    indeterminate={selected.length > 0 && selected.length < workOrders.length}
                    checked={workOrders.length > 0 && selected.length === workOrders.length}
                    onChange={handleSelectAllClick}
                  />
                </TableCell>
                <TableCell>
                  <TableSortLabel
                    active={orderBy === 'workOrderId'}
                    direction={order}
                    onClick={() => handleRequestSort('workOrderId')}
                  >
                    Task ID
                  </TableSortLabel>
                </TableCell>
                <TableCell>
                  <TableSortLabel
                    active={orderBy === 'taskType'}
                    direction={order}
                    onClick={() => handleRequestSort('taskType')}
                  >
                    Task Type
                  </TableSortLabel>
                </TableCell>
                <TableCell>Description</TableCell>
                <TableCell>
                  <TableSortLabel
                    active={orderBy === 'roomNumber'}
                    direction={order}
                    onClick={() => handleRequestSort('roomNumber')}
                  >
                    Room Number
                  </TableSortLabel>
                </TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Priority</TableCell>
                <TableCell>Assigned To</TableCell>
                <TableCell>
                  <TableSortLabel
                    active={orderBy === 'createdDate'}
                    direction={order}
                    onClick={() => handleRequestSort('createdDate')}
                  >
                    Created Date
                  </TableSortLabel>
                </TableCell>
                <TableCell>Age (Days)</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {paginatedWorkOrders.map((order) => {
                const isItemSelected = isSelected(order._id);  // Use _id for selection
                return (
                  <TableRow
                    hover
                    role="checkbox"
                    aria-checked={isItemSelected}
                    tabIndex={-1}
                    key={order._id}
                    selected={isItemSelected}
                  >
                    <TableCell padding="checkbox">
                      <Checkbox
                        checked={isItemSelected}
                        onClick={(event) => handleCheckboxClick(event, order._id)}  // Use _id for selection
                      />
                    </TableCell>
                    <TableCell>{order.workOrderId}</TableCell> {/* Display workOrderId */}
                    <TableCell>{order.taskType}</TableCell>
                    <TableCell>{order.description}</TableCell>
                    <TableCell>{order.roomNumber}</TableCell>
                    <TableCell>{order.status}</TableCell>
                    <TableCell>{order.priority}</TableCell>
                    <TableCell>
                      {order.assignedTo ? order.assignedTo.name : 'Unassigned'}
                    </TableCell>
                    <TableCell>{new Date(order.createdDate).toLocaleDateString()}</TableCell>
                    <TableCell>{order.age}</TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableContainer>

{/* Pagination Controls */}
<TablePagination
  component="div"
  count={workOrders.length}
  page={page}
  onPageChange={handleChangePage}
  rowsPerPage={rowsPerPage}
  onRowsPerPageChange={handleChangeRowsPerPage}
  rowsPerPageOptions={[10, 25, 50, 100]}
/>

</Paper>

{/* Modal for creating new work orders */}
<WorkOrderFormModal 
  open={openModal} 
  handleClose={handleCloseModal} 
  onWorkOrderSubmit={fetchWorkOrders} // Pass the fetchWorkOrders function as a prop
/>

{/* Modal for editing existing work orders */}
<EditWorkOrderModal 
  open={openEditModal} 
  handleClose={handleCloseEditModal} 
  workOrder={selectedWorkOrder} // Pass the selected work order for editing
  onWorkOrderUpdate={fetchWorkOrders} // Refresh the work orders after updating
/>

    </Box>
  );
};

export default WorkOrderManagement;
