// src/theme.js
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    mode: 'dark', // Dark mode
    primary: {
      main: '#90caf9', // Lighter blue for accents (optional)
    },
    secondary: {
      main: '#f48fb1', // Subtle accent color (optional)
    },
    background: {
      default: '#0c0e13', // Darker background color
      paper: '#121212',   // Dark card and modal background
    },
    text: {
      primary: '#e0e0e0', // Light gray text for readability
      secondary: '#b0b0b0', // Muted text for secondary content
    },
  },
  typography: {
    fontFamily: 'Roboto, sans-serif',
    h1: {
      fontSize: '2.2rem',
      fontWeight: 600,
      color: '#ffffff', // Ensure headers are white
    },
    h2: {
      fontSize: '1.8rem',
      fontWeight: 500,
      color: '#ffffff',
    },
    body1: {
      color: '#e0e0e0', // Ensure normal text is lighter
    },
    button: {
      textTransform: 'none', // Keep buttons lowercase
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none', // No uppercase for buttons
          borderRadius: '8px',   // Slightly rounder buttons
          backgroundColor: '#1c1c1c', // Darker button background
          '&:hover': {
            backgroundColor: '#333', // Darker hover effect
          },
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          backgroundColor: '#1e1e1e', // Darker input background
          borderRadius: '6px',
          color: '#ffffff', // Ensure text input color is light
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          backgroundColor: '#121212', // Darker background for cards and dialogs
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        colorPrimary: {
          backgroundColor: '#121212', // Darker app bar
        },
      },
    },
  },
});

export default theme;
