import React, { useState, useEffect } from 'react';
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Grid,
  Tabs,
  Tab,
  Box,
  Typography,
  IconButton,
  Snackbar
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import CloseIcon from '@mui/icons-material/Close';  // Import CloseIcon for "X" button
import axios from '../api/axiosConfig';

// Internal status values
const statusMapping = {
  new: 'New',
  pending: 'Pending',
  delayed: 'Delayed',
  closed: 'Closed',
  excluded: 'Excluded',
  're-opened': 'Re-Opened',
};

const taskTypes = [
  'Clean Up / Spill',
  'Cooling Issue',
  'Electrical Issue',
  'Equipment Repairs',
  'Fire Safety',
  'General Maintenance Request',
  'Health and Safety',
  'Heating Issues',
  'Lighting Issues',
  'Mechanical Issues',
  'Painting / Touch Ups',
  'Pest Control',
  'Plumbing Issues',
  'Waste Issues',
];

const EditWorkOrderModal = ({ open, handleClose, workOrder, fetchWorkOrders }) => {
  const [formData, setFormData] = useState({
    status: '',
    taskType: '',
    roomNumber: '',
    description: '',
    notes: '',
    submittedBy: '',
    notesHistory: [], // Initialize notesHistory
    newNote: '',
  });

  const [selectedTab, setSelectedTab] = useState(0);
  const [snackbarOpen, setSnackbarOpen] = useState(false);  // Snackbar visibility
  const [originalFormData, setOriginalFormData] = useState(null);  // Store original form data before changes

  useEffect(() => {
    if (workOrder) {
      // Prepopulate the form with existing work order data, including notes history
      setFormData({
        status: workOrder.status || '',
        taskType: workOrder.taskType || '',
        roomNumber: workOrder.roomNumber || '',
        description: workOrder.details || '',
        notes: workOrder.notes || '',
        submittedBy: workOrder.submittedBy || '',
        notesHistory: workOrder.notesHistory || [], // Make sure notesHistory is included
        newNote: '',
      });

      // Store original form data to enable undo functionality
      setOriginalFormData({
        status: workOrder.status || '',
        taskType: workOrder.taskType || '',
        roomNumber: workOrder.roomNumber || '',
        description: workOrder.details || '',
        notes: workOrder.notes || '',
        submittedBy: workOrder.submittedBy || '',
        notesHistory: workOrder.notesHistory || [],
      });
    }
  }, [workOrder]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  // Separate API call to add a note using /add-note endpoint, but it will not close the modal
  const handleAddNote = async () => {
    if (formData.newNote.trim() === '') {
      return; // Prevent adding empty notes
    }

    try {
      const newNote = {
        note: formData.newNote,
      };

      // Send the new note to the backend using the dedicated route
      const response = await axios.put(`/api/work-orders/${workOrder._id}/add-note`, newNote);

      // Update the local notes history with the new note from the backend response
      setFormData({
        ...formData,
        notesHistory: response.data.notesHistory,
        newNote: '',
      });

    } catch (error) {
      console.error('Error adding note:', error);
      alert('Failed to add the note. Please try again.');
    }
  };

  // Delete a note from the notes history, but it will not close the modal
  const handleDeleteNote = async (index) => {
    const updatedNotesHistory = formData.notesHistory.filter((_, i) => i !== index);

    try {
      await axios.put(`/api/work-orders/${workOrder._id}`, {
        notesHistory: updatedNotesHistory
      });

      setFormData({
        ...formData,
        notesHistory: updatedNotesHistory,
      });

    } catch (error) {
      console.error('Error deleting note:', error);
      alert('Failed to delete the note. Please try again.');
    }
  };

  // Save the work order when pressing "Save" button and close the modal
  const handleSubmit = async () => {
    try {
      // Save the work order with updated information
      await axios.put(`/api/work-orders/${workOrder._id}`, {
        status: formData.status,
        taskType: formData.taskType,
        roomNumber: formData.roomNumber,
        details: formData.description,  // Ensure 'details' is updated, not 'description'
        priority: formData.priority,    // Add priority if necessary
        notesHistory: formData.notesHistory,
      });

      // Open the Snackbar to show success and provide an Undo option
      setSnackbarOpen(true);
      fetchWorkOrders();
      handleClose();  // Close the modal after saving

    } catch (error) {
      console.error('Error updating work order:', error);
      alert('Failed to update the work order. Please try again.');
    }
  };

  // Handle closing of the Snackbar
  const handleCloseSnackbar = (event, reason) => {
    if (reason === 'clickaway') {
      return; // Ignore clicks outside the snackbar
    }
    setSnackbarOpen(false); // Allow manual close
  };

  // Handle Undo action
  const handleUndo = async () => {
    try {
      // Restore original form data by calling the backend with the original data
      await axios.put(`/api/work-orders/${workOrder._id}`, originalFormData);

      // Re-fetch work orders to update the UI
      fetchWorkOrders();

      // Close the Snackbar after undoing the action
      handleCloseSnackbar();  // Explicitly call the function to close the Snackbar

    } catch (error) {
      console.error('Error undoing changes:', error);
    }
  };

  return (
    <>
      <Dialog open={open} onClose={handleClose} fullWidth maxWidth="md">
        <DialogTitle>Edit Work Order</DialogTitle>
        <DialogContent>
          <DialogContentText>Update the work order details below:</DialogContentText>

          {/* Tabs for switching between Details and Notes */}
          <Tabs value={selectedTab} onChange={handleTabChange}>
            <Tab label="Work Order Details" />
            <Tab label="Notes and Updates" />
          </Tabs>

          {selectedTab === 0 && (
            <Box>
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <FormControl fullWidth margin="normal">
                    <InputLabel>Status</InputLabel>
                    <Select
                      name="status"
                      value={formData.status}
                      onChange={handleChange}
                      fullWidth
                    >
                      {Object.keys(statusMapping).map((status) => (
                        <MenuItem key={status} value={status}>
                          {statusMapping[status]} {/* Display name */}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  <FormControl fullWidth margin="normal">
                    <InputLabel>Task Type</InputLabel>
                    <Select
                      name="taskType"
                      value={formData.taskType}
                      onChange={handleChange}
                      fullWidth
                    >
                      {taskTypes.map((type) => (
                        <MenuItem key={type} value={type}>
                          {type}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  <TextField
                    label="Room Number"
                    name="roomNumber"
                    value={formData.roomNumber}
                    onChange={handleChange}
                    fullWidth
                    margin="normal"
                  />

                  <TextField
                    label="Submitted By"
                    name="submittedBy"
                    value={formData.submittedBy?.username || 'N/A'}
                    disabled
                    fullWidth
                    margin="normal"
                  />
                </Grid>

                <Grid item xs={6}>
                  <TextField
                    label="Original Description"
                    name="description"
                    value={formData.description}
                    fullWidth
                    multiline
                    rows={6}
                    margin="normal"
                    disabled
                  />
                </Grid>
              </Grid>
            </Box>
          )}

          {selectedTab === 1 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Add new note
              </Typography>
              <TextField
                label="Add new note"
                name="newNote"
                value={formData.newNote}
                onChange={handleChange}
                fullWidth
                multiline
                rows={3}
                margin="normal"
              />
              <Button variant="contained" color="primary" onClick={handleAddNote}>
                Add Note
              </Button>

              <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
                Notes History
              </Typography>
              <Box sx={{ mt: 2 }}>
                {formData.notesHistory.map((note, index) => (
                  <Box key={index} sx={{ mb: 2, borderBottom: '1px solid #ccc', pb: 1 }}>
                    <Typography variant="body2">
                      <strong>{note.username}</strong> at{' '}
                      {new Date(note.timestamp).toLocaleString()}:
                    </Typography>
                    <Typography
                      variant="body1"
                      sx={{ whiteSpace: 'pre-wrap' }}
                    >
                      {note.note}
                    </Typography>
                    <IconButton size="small" onClick={() => handleDeleteNote(index)}>
                      <DeleteIcon fontSize="small" />
                    </IconButton>
                  </Box>
                ))}
              </Box>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleSubmit} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for showing success message with Undo option and an "X" to close */}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={7500}
        onClose={handleCloseSnackbar}
        message="Work order updated"
        action={
          <>
            <Button color="inherit" size="small" onClick={handleUndo}>
              Undo
            </Button>
            <IconButton
              size="small"
              aria-label="close"
              color="inherit"
              onClick={handleCloseSnackbar}
            >
              <CloseIcon />
            </IconButton>
          </>
        }
      />
    </>
  );
};

export default EditWorkOrderModal;
