import React, { useState, useEffect } from 'react';
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Grid,  // Add Grid from Material-UI
  Box
} from '@mui/material';
import axios from '../api/axiosConfig';

const statuses = ['new', 'pending', 'delayed', 'closed', 'excluded', 're-opened'];
const taskTypes = [
  'Clean Up / Spill',
  'Cooling Issue',
  'Electrical Issue',
  'Equipment Repairs',
  'Fire Safety',
  'General Maintenance Request',
  'Health and Safety',
  'Heating Issues',
  'Lighting Issues',
  'Mechanical Issues',
  'Painting / Touch Ups',
  'Pest Control',
  'Plumbing Issues',
  'Waste Issues',
];

const EditWorkOrderModal = ({ open, handleClose, workOrder, fetchWorkOrders }) => {
  const [formData, setFormData] = useState({
    status: '',
    taskType: '',
    roomNumber: '',
    description: '',
    notes: '',
    submittedBy: ''
  });

  useEffect(() => {
    if (workOrder) {
      // Prepopulate the form with existing work order data
      setFormData({
        status: workOrder.status || '',
        taskType: workOrder.taskType || '',
        roomNumber: workOrder.roomNumber || '',
        description: workOrder.details || '',
        notes: workOrder.notes || '',
        submittedBy: workOrder.submittedBy || ''
      });
    }
  }, [workOrder]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async () => {
    try {
      // Make the API call to update the work order
      await axios.put(`/api/work-orders/${workOrder._id}`, formData);

      // Notify the user that the work order was successfully updated
      alert('Work order updated successfully!');

      // Call fetchWorkOrders to refresh the work order list
      fetchWorkOrders();  // This is the correct function passed as a prop

      // Close the modal after successfully updating
      handleClose();
    } catch (error) {
      // Handle any errors during the update process
      console.error('Error updating work order:', error);
      alert('Failed to update the work order. Please try again.');
    }
  };

  return (
    <Dialog open={open} onClose={handleClose} fullWidth maxWidth="md"> {/* Adjust size of modal */}
      <DialogTitle>Edit Work Order</DialogTitle>
      <DialogContent>
        <DialogContentText>
          Update the work order details below:
        </DialogContentText>

        {/* Grid layout for left and right alignment */}
        <Grid container spacing={2}>
          {/* Left side fields */}
          <Grid item xs={6}>
            {/* Status */}
            <FormControl fullWidth margin="normal">
              <InputLabel>Status</InputLabel>
              <Select
                name="status"
                value={formData.status}
                onChange={handleChange}
                fullWidth
              >
                {statuses.map((status) => (
                  <MenuItem key={status} value={status}>
                    {status}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Task Type */}
            <FormControl fullWidth margin="normal">
              <InputLabel>Task Type</InputLabel>
              <Select
                name="taskType"
                value={formData.taskType}
                onChange={handleChange}
                fullWidth
              >
                {taskTypes.map((type) => (
                  <MenuItem key={type} value={type}>
                    {type}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Room Number */}
            <TextField
              label="Room Number"
              name="roomNumber"
              value={formData.roomNumber}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            {/* Submitted By */}
            <TextField
              label="Submitted By"
              name="submittedBy"
              value={formData.submittedBy}
              disabled
              fullWidth
              margin="normal"
            />
          </Grid>

          {/* Right side for Original Description and Notes */}
          <Grid item xs={6}>
            {/* Original Description (unmodifiable) */}
            <TextField
              label="Original Description"
              name="description"
              value={formData.description}
              fullWidth
              multiline
              rows={6}  // Adjusted to make it larger
              margin="normal"
              disabled  // Make it unmodifiable
            />

            {/* Notes */}
            <TextField
              label="Notes"
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              fullWidth
              multiline
              rows={6}  // Adjusted to make it larger 
              margin="normal"
            />
          </Grid>
        </Grid>

      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="secondary">
          Cancel
        </Button>
        <Button onClick={handleSubmit} color="primary">
          Save
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default EditWorkOrderModal;
