// src/theme.js
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    mode: 'dark', // Switch to dark mode
    primary: {
      main: '#1976d2', // Adjust primary color (blue)
    },
    secondary: {
      main: '#9c27b0', // Adjust secondary color (purple)
    },
    background: {
      default: '#0a1929', // Dark background
      paper: '#001e3c',   // Card background
    },
    text: {
      primary: '#ffffff', // Light text for dark background
    },
  },
  typography: {
    fontFamily: 'Roboto, sans-serif',
    h1: {
      fontSize: '2.2rem',
      fontWeight: 600,
      color: '#ffffff', // Ensure headers are white
    },
    h2: {
      fontSize: '1.8rem',
      fontWeight: 500,
      color: '#ffffff',
    },
    // Add more custom typography if needed
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none', // No uppercase for buttons
          borderRadius: '6px',   // Rounded buttons like in the template
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          backgroundColor: '#0a1929', // Dark input background
          borderRadius: '6px',
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          backgroundColor: '#001e3c', // Dark background for paper components
        },
      },
    },
  },
});

export default theme;
