import React, { useEffect, useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import axios from '../api/axiosConfig';
import AddUserModal from './AddUserModal';
import EditUserModal from './EditUserModal';

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [openAddModal, setOpenAddModal] = useState(false);  // State to control AddUserModal visibility
  const [selectedUser, setSelectedUser] = useState(null);   // State to control EditUserModal visibility

  // Fetch all users
  const fetchUsers = () => {
    axios.get('/api/users')
      .then(response => setUsers(response.data))
      .catch(error => console.error(error));
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Open Edit User Modal
  const handleEditUser = (user) => {
    setSelectedUser(user);
  };

  // Close the AddUserModal
  const handleCloseAddModal = () => {
    setOpenAddModal(false);
  };

  // Close the EditUserModal
  const handleCloseEditModal = () => {
    setSelectedUser(null);
  };

  return (
    <>
      {/* Add User Button */}
      <Button variant="contained" color="primary" onClick={() => setOpenAddModal(true)}>
        Add New User
      </Button>

      {/* AddUserModal */}
      <AddUserModal open={openAddModal} handleClose={handleCloseAddModal} fetchUsers={fetchUsers} />

      {/* EditUserModal */}
      {selectedUser && (
        <EditUserModal
          open={Boolean(selectedUser)}
          handleClose={handleCloseEditModal}
          user={selectedUser}
          fetchUsers={fetchUsers}
        />
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Username</TableCell>
              <TableCell>Role</TableCell>
              <TableCell>First Name</TableCell>
              <TableCell>Last Name</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {users.map(user => (
              <TableRow key={user._id}>
                <TableCell>{user.username}</TableCell>
                <TableCell>{user.role}</TableCell>
                <TableCell>{user.firstName || 'N/A'}</TableCell>
                <TableCell>{user.lastName || 'N/A'}</TableCell>
                <TableCell>
                  <Button variant="outlined" onClick={() => handleEditUser(user)}>Edit</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
};

export default UserManagement;
