import React, { useState, useEffect } from 'react';
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  MenuItem,
  Select,
  InputLabel,
  FormControl
} from '@mui/material';
import axios from '../api/axiosConfig';

const statuses = ['new', 'pending', 'delayed', 'closed', 'excluded', 're-opened'];

const taskTypes = [
  'Clean Up / Spill',
  'Cooling Issue',
  'Electrical Issue',
  'Equipment Repairs',
  'Fire Safety',
  'General Maintenance Request',
  'Health and Safety',
  'Heating Issues',
  'Lighting Issues',
  'Mechanical Issues',
  'Painting / Touch Ups',
  'Pest Control',
  'Plumbing Issues',
  'Waste Issues',
];

const EditWorkOrderModal = ({ open, handleClose, workOrder, fetchWorkOrders }) => {
  const [formData, setFormData] = useState({
    status: '',
    taskType: '',
    roomNumber: '',
    description: '',
    notes: '',
    submittedBy: ''
  });

  useEffect(() => {
    if (workOrder) {
      // Prepopulate the form with existing work order data
      setFormData({
        status: workOrder.status || '',
        taskType: workOrder.taskType || '',
        roomNumber: workOrder.roomNumber || '',
        description: workOrder.details || '',
        notes: workOrder.notes || '',
        submittedBy: workOrder.submittedBy || ''
      });
    }
  }, [workOrder]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

const handleSubmit = async () => {
  try {
    // Make the API call to update the work order
    await axios.put(`/api/work-orders/${workOrder._id}`, formData);

    // Notify the user that the work order was successfully updated
    alert('Work order updated successfully!');

    // Call the onWorkOrderSubmit function if it's passed as a prop (to refresh the work order list)
    if (typeof onWorkOrderSubmit === 'function') {
      onWorkOrderSubmit(); // Use this function to refresh the work order list
    } else {
      console.warn('onWorkOrderSubmit function is not passed as a prop');
    }

    // Close the modal after successfully updating
    handleClose();
  } catch (error) {
    // Handle any errors during the update process
    console.error('Error updating work order:', error);
    alert('Failed to update the work order. Please try again.');
  }
};


  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>Edit Work Order</DialogTitle>
      <DialogContent>
        <DialogContentText>
          Update the work order details below:
        </DialogContentText>

        {/* Status */}
        <FormControl fullWidth margin="normal">
          <InputLabel>Status</InputLabel>
          <Select
            name="status"
            value={formData.status}
            onChange={handleChange}
            fullWidth
          >
            {statuses.map((status) => (
              <MenuItem key={status} value={status}>
                {status}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Task Type */}
        <FormControl fullWidth margin="normal">
          <InputLabel>Task Type</InputLabel>
          <Select
            name="taskType"
            value={formData.taskType}
            onChange={handleChange}
            fullWidth
          >
            {taskTypes.map((type) => (
              <MenuItem key={type} value={type}>
                {type}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Room Number */}
        <TextField
          label="Room Number"
          name="roomNumber"
          value={formData.roomNumber}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />

        {/* Original Description */}
        <TextField
          label="Original Description"
          name="description"
          value={formData.description}
          onChange={handleChange}
          fullWidth
          multiline
          rows={4}
          margin="normal"
        />

        {/* Notes */}
        <TextField
          label="Notes"
          name="notes"
          value={formData.notes}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />

        {/* Submitted By */}
        <TextField
          label="Submitted By"
          name="submittedBy"
          value={formData.submittedBy}
          disabled
          fullWidth
          margin="normal"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="secondary">
          Cancel
        </Button>
        <Button onClick={handleSubmit} color="primary">
          Save
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default EditWorkOrderModal;
