import React, { useEffect, useState } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  TablePagination,
  TableSortLabel,
  Checkbox,
  Toolbar,
  IconButton,
  Tooltip,
  Box,
  Fab,
  Switch,
  FormControlLabel,
  Collapse,
  Button,
  TextField,
  MenuItem,
} from '@mui/material';
import { alpha } from '@mui/material/styles';
import { DatePicker, LocalizationProvider } from '@mui/x-date-pickers';
import AdapterDateFns from '@mui/x-date-pickers/AdapterDateFns'; // Adapter for date-fns
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import axios from '../api/axiosConfig';
import WorkOrderFormModal from './WorkOrderFormModal';
import EditWorkOrderModal from './EditWorkOrderModal';

// Helper function to capitalize the first letter
const capitalizeFirstLetter = (string) => {
  return string.charAt(0).toUpperCase() + string.slice(1);
};

const taskTypes = [
  'Clean Up / Spill',
  'Cooling Issue',
  'Electrical Issue',
  'Equipment Repairs',
  'Fire Safety',
  'General Maintenance Request',
  'Health and Safety',
  'Heating Issues',
  'Lighting Issues',
  'Mechanical Issues',
  'Painting / Touch Ups',
  'Pest Control',
  'Plumbing Issues',
  'Waste Issues',
];

const priorities = ['Low', 'Medium', 'High', 'Emergency'];

const statusOptions = ['New', 'Pending', 'Delayed', 'Closed', 'Excluded', 'Re-Opened'];

const WorkOrderManagement = () => {
  const [workOrders, setWorkOrders] = useState([]);
  const [openModal, setOpenModal] = useState(false);
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [selectedWorkOrder, setSelectedWorkOrder] = useState(null);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [order, setOrder] = useState('asc');
  const [orderBy, setOrderBy] = useState('taskType');
  const [selected, setSelected] = useState([]);
  const [dense, setDense] = useState(true); // Work Order Table is set to dense by default
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  // Filter states
  const [filterExpanded, setFilterExpanded] = useState(false);
  const [fromDate, setFromDate] = useState(null);
  const [toDate, setToDate] = useState(null);
  const [selectedStatus, setSelectedStatus] = useState('');
  const [selectedTaskType, setSelectedTaskType] = useState('');
  const [selectedPriority, setSelectedPriority] = useState('');

  useEffect(() => {
    fetchCurrentUser();
  }, []);

  const fetchCurrentUser = async () => {
    try {
      const response = await axios.get('/api/auth/me');
      const { role } = response.data.user;

      if (role === 'Admin' || role === 'Technician' || role === 'Tenant') {
        fetchWorkOrders();
      } else {
        setError('You do not have access to view this page.');
      }
    } catch (error) {
      setError('Failed to fetch current user.');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchWorkOrders = () => {
    axios
      .get('/api/work-orders')
      .then((response) => setWorkOrders(response.data))
      .catch((error) => console.error(error));
  };

  const handleOpenModal = () => setOpenModal(true);
  const handleCloseModal = () => setOpenModal(false);

  const handleOpenEditModal = (workOrder) => {
    setSelectedWorkOrder(workOrder);
    setEditModalOpen(true);
  };

  const handleCloseEditModal = () => {
    setEditModalOpen(false);
    setSelectedWorkOrder(null);
  };

  const handleRequestSort = (property) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  const sortComparator = (a, b) => {
    if (!a[orderBy] || !b[orderBy]) return 0;

    if (typeof a[orderBy] === 'number' && typeof b[orderBy] === 'number') {
      return order === 'asc' ? a[orderBy] - b[orderBy] : b[orderBy] - a[orderBy];
    } else if (typeof a[orderBy] === 'string' && typeof b[orderBy] === 'string') {
      return order === 'asc' ? a[orderBy].localeCompare(b[orderBy]) : b[orderBy].localeCompare(a[orderBy]);
    }

    return 0;
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleSelectAllClick = (event) => {
    if (event.target.checked) {
      const newSelecteds = workOrders.map((n) => n._id);
      setSelected(newSelecteds);
      return;
    }
    setSelected([]);
  };

  const handleCheckboxClick = (event, _id) => {
    const selectedIndex = selected.indexOf(_id);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, _id);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1)
      );
    }

    setSelected(newSelected);
  };

  const isSelected = (_id) => selected.indexOf(_id) !== -1;

  const handleDeleteSelected = () => {
    if (selected.length === 0) {
      console.error('No work orders selected for deletion');
      return;
    }

    const promises = selected.map((_id) =>
      axios
        .delete(`/api/work-orders/${_id}`)
        .then(() => {
          console.log(`Deleted work order with _id: ${_id}`);
        })
        .catch((error) => {
          console.error(
            `Failed to delete work order with _id: ${_id}`,
            error.response ? error.response.data : error.message
          );
        })
    );

    Promise.all(promises)
      .then(() => {
        fetchWorkOrders();
        setSelected([]);
      })
      .catch((error) => console.error('Failed to complete deletion of work orders', error));
  };

  const applyFilters = () => {
    let filteredWorkOrders = [...workOrders];

    // Date range filtering
    if (fromDate && toDate) {
      filteredWorkOrders = filteredWorkOrders.filter((order) => {
        const createdDate = new Date(order.createdDate);
        return createdDate >= new Date(fromDate) && createdDate <= new Date(toDate);
      });
    }

    // Status filtering
    if (selectedStatus) {
      filteredWorkOrders = filteredWorkOrders.filter((order) => order.status === selectedStatus);
    }

    // Task type filtering
    if (selectedTaskType) {
      filteredWorkOrders = filteredWorkOrders.filter((order) => order.taskType === selectedTaskType);
    }

    // Priority filtering
    if (selectedPriority) {
      filteredWorkOrders = filteredWorkOrders.filter((order) => order.priority === selectedPriority);
    }

    return filteredWorkOrders;
  };

  const filteredWorkOrders = applyFilters();
  const paginatedWorkOrders = filteredWorkOrders
    .sort(sortComparator)
    .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage);

  if (isLoading) {
    return <Typography>Loading...</Typography>;
  }

  if (error) {
    return <Typography>{error}</Typography>;
  }

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <Box sx={{ width: '100%' }}>
        <Paper sx={{ width: '100%', mb: 2 }}>
          <Toolbar
            sx={[
              { pl: { sm: 2 }, pr: { xs: 1, sm: 1 } },
              selected.length > 0 && {
                bgcolor: (theme) =>
                  alpha(theme.palette.primary.main, theme.palette.action.activatedOpacity),
              },
            ]}
          >
            {selected.length > 0 ? (
              <Typography
                sx={{ flex: '1 1 100%' }}
                color="inherit"
                variant="subtitle1"
                component="div"
              >
                {selected.length} selected
              </Typography>
            ) : (
              <Typography
                sx={{ flex: '1 1 100%' }}
                variant="h6"
                id="tableTitle"
                component="div"
              >
                Work Orders
              </Typography>
            )}

            <Fab
              color="primary"
              aria-label="add"
              onClick={handleOpenModal}
              sx={{
                ml: 2,
                width: 45,
                height: 45,
                borderRadius: '50%',
                minWidth: '45px',
                minHeight: '45px',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <AddIcon />
            </Fab>

            {selected.length > 0 && (
              <Tooltip title="Delete">
                <IconButton onClick={handleDeleteSelected}>
                  <DeleteIcon />
                </IconButton>
              </Tooltip>
            )}
          </Toolbar>

          {/* Filter Toggle Button */}
          <Toolbar sx={{ pl: { sm: 2 }, pr: { xs: 1, sm: 1 } }}>
            <Typography variant="h6" sx={{ flex: '1 1 100%' }}>
              Work Orders
            </Typography>
            <IconButton onClick={() => setFilterExpanded(!filterExpanded)}>
              {filterExpanded ? <ExpandLessIcon /> : <ExpandMoreIcon />}
            </IconButton>
          </Toolbar>

          {/* Filter Options */}
          <Collapse in={filterExpanded} timeout="auto" unmountOnExit>
            <Box sx={{ p: 2 }}>
              <Box sx={{ display: 'flex', gap: 2 }}>
                <DatePicker
                  label="From Date"
                  value={fromDate}
                  onChange={(newValue) => setFromDate(newValue)}
                  renderInput={(params) => <TextField {...params} fullWidth />}
                />
                <DatePicker
                  label="To Date"
                  value={toDate}
                  onChange={(newValue) => setToDate(newValue)}
                  renderInput={(params) => <TextField {...params} fullWidth />}
                />
              </Box>

              <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
                <TextField
                  label="Status"
                  select
                  value={selectedStatus}
                  onChange={(e) => setSelectedStatus(e.target.value)}
                  fullWidth
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {statusOptions.map((status) => (
                    <MenuItem key={status} value={status}>
                      {status}
                    </MenuItem>
                  ))}
                </TextField>

                <TextField
                  label="Task Type"
                  select
                  value={selectedTaskType}
                  onChange={(e) => setSelectedTaskType(e.target.value)}
                  fullWidth
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {taskTypes.map((task) => (
                    <MenuItem key={task} value={task}>
                      {task}
                    </MenuItem>
                  ))}
                </TextField>

                <TextField
                  label="Priority"
                  select
                  value={selectedPriority}
                  onChange={(e) => setSelectedPriority(e.target.value)}
                  fullWidth
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {priorities.map((priority) => (
                    <MenuItem key={priority} value={priority}>
                      {priority}
                    </MenuItem>
                  ))}
                </TextField>
              </Box>

              <Button variant="contained" sx={{ mt: 2 }} onClick={applyFilters}>
                Search
              </Button>
            </Box>
          </Collapse>

          {/* Table */}
          <TableContainer>
            <Table size={dense ? 'small' : 'medium'}>
              <TableHead>
                <TableRow>
                  <TableCell padding="checkbox">
                    <Checkbox
                      indeterminate={selected.length > 0 && selected.length < workOrders.length}
                      checked={workOrders.length > 0 && selected.length === workOrders.length}
                      onChange={(event) => setSelected(event.target.checked ? workOrders.map((n) => n._id) : [])}
                    />
                  </TableCell>
                  <TableCell>
                    <TableSortLabel active={orderBy === 'workOrderId'} direction={order} onClick={() => handleRequestSort('workOrderId')}>
                      Task ID
                    </TableSortLabel>
                  </TableCell>
                  <TableCell>Task Type</TableCell>
                  <TableCell>Description</TableCell>
                  <TableCell>Room Number</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Priority</TableCell>
                  <TableCell>Created Date</TableCell>
                  <TableCell>Age (Days)</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {paginatedWorkOrders.map((order) => (
                  <TableRow key={order._id} hover role="checkbox" onDoubleClick={() => handleOpenEditModal(order)}>
                    <TableCell padding="checkbox">
                      <Checkbox checked={isSelected(order._id)} onClick={(event) => handleCheckboxClick(event, order._id)} />
                    </TableCell>
                    <TableCell>{order.workOrderId}</TableCell>
                    <TableCell>{order.taskType}</TableCell>
                    <TableCell>{order.details}</TableCell>
                    <TableCell>{order.roomNumber}</TableCell>
                    <TableCell>{capitalizeFirstLetter(order.status)}</TableCell>
                    <TableCell>{order.priority}</TableCell>
                    <TableCell>{new Date(order.createdDate).toLocaleDateString()}</TableCell>
                    <TableCell>{order.age}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>

          <TablePagination
            component="div"
            count={filteredWorkOrders.length}
            page={page}
            onPageChange={handleChangePage}
            rowsPerPage={rowsPerPage}
            onRowsPerPageChange={handleChangeRowsPerPage}
          />
        </Paper>

        <FormControlLabel control={<Switch checked={dense} onChange={(e) => setDense(e.target.checked)} />} label="Dense padding" />

        <WorkOrderFormModal open={openModal} handleClose={handleCloseModal} onWorkOrderSubmit={fetchWorkOrders} />

        <EditWorkOrderModal open={editModalOpen} handleClose={handleCloseEditModal} workOrder={selectedWorkOrder} fetchWorkOrders={fetchWorkOrders} />
      </Box>
    </LocalizationProvider>
  );
};

export default WorkOrderManagement;
