// src/theme.js

import { createTheme } from '@mui/material/styles';

// Custom dark theme configuration
const theme = createTheme({
  palette: {
    mode: 'dark',  // Enables dark mode
    primary: {
      main: '#90caf9',  // Soft blue
      dark: '#1e88e5',  // Darker shade of blue
      light: '#e3f2fd', // Lighter shade of blue
    },
    secondary: {
      main: '#f48fb1',  // Light pink
      dark: '#c2185b',  // Darker pink for accents
      light: '#f8bbd0', // Lighter pink for hover effects
    },
    background: {
      default: '#121212', // Deep dark background
      paper: '#1e1e1e',   // Slightly lighter background for paper/cards
    },
    text: {
      primary: '#ffffff',  // White text for high contrast
      secondary: '#b0bec5', // Soft grey for secondary text
      disabled: '#757575',  // Slightly dimmed for disabled elements
    },
    error: {
      main: '#f44336',  // Standard error red
    },
    warning: {
      main: '#ffa726',  // Warning orange
    },
    info: {
      main: '#29b6f6',  // Info blue
    },
    success: {
      main: '#66bb6a',  // Success green
    },
    action: {
      active: '#ffffff', // White for active elements like buttons or icons
      hover: 'rgba(255, 255, 255, 0.08)', // Light hover effect
      selected: 'rgba(255, 255, 255, 0.16)', // Light selection effect
      disabled: 'rgba(255, 255, 255, 0.3)', // Dimmed disabled elements
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none', // Disable uppercase transformation for button text
          fontWeight: 'bold',
        },
        containedPrimary: {
          backgroundColor: '#1e88e5',
          '&:hover': {
            backgroundColor: '#1565c0',
          },
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        colorPrimary: {
          backgroundColor: '#1e1e1e',
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          backgroundColor: '#1e1e1e',
        },
      },
    },
  },
});

export default theme;
