import { createTheme } from '@mui/material/styles';

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#bb86fc', // Light purple (primary color for dark theme)
      dark: '#3700b3', // Darker shade of purple
    },
    secondary: {
      main: '#03dac6', // Teal color
      dark: '#018786',
    },
    background: {
      default: '#121212', // Dark background
      paper: '#1d1d1d', // Surface color for components
    },
    error: {
      main: '#cf6679', // Error color
    },
    text: {
      primary: '#ffffff', // White text for primary elements
      secondary: '#b0bec5', // Gray text for secondary elements
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none', // Remove all caps in buttons
        },
      },
    },
  },
});
