import React, { useEffect, useState } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Typography,
} from '@mui/material';
import axios from '../api/axiosConfig';
import WorkOrderFormModal from './WorkOrderFormModal'; // Import the Work Order Modal

const WorkOrderManagement = () => {
  const [workOrders, setWorkOrders] = useState([]);
  const [openModal, setOpenModal] = useState(false); // State to control modal visibility

  // Fetch the work orders
  useEffect(() => {
    fetchWorkOrders();
  }, []);

  const fetchWorkOrders = () => {
    axios
      .get('/api/work-orders')
      .then((response) => setWorkOrders(response.data))
      .catch((error) => console.error(error));
  };

  // Handle modal open and close
  const handleOpenModal = () => setOpenModal(true);
  const handleCloseModal = () => {
    setOpenModal(false);
    fetchWorkOrders(); // Refresh work orders after closing modal
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Work Orders Management
      </Typography>

      {/* Button to open modal */}
      <Button variant="contained" color="primary" onClick={handleOpenModal}>
        Create New Work Order
      </Button>

      {/* Modal for creating new work orders */}
      <WorkOrderFormModal open={openModal} handleClose={handleCloseModal} />

      {/* Work Orders Table */}
      <TableContainer component={Paper} sx={{ marginTop: 3 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Task Type</TableCell>
              <TableCell>Description</TableCell>
              <TableCell>Room Number</TableCell>
              <TableCell>Status</TableCell>
              <TableCell>Priority</TableCell>
              <TableCell>Assigned To</TableCell>
              <TableCell>Created Date</TableCell>
              <TableCell>Age (Days)</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {workOrders.map((order) => (
              <TableRow key={order._id}>
                <TableCell>{order._id}</TableCell>
                <TableCell>{order.taskType}</TableCell>
                <TableCell>{order.description}</TableCell>
                <TableCell>{order.roomNumber}</TableCell>
                <TableCell>{order.status}</TableCell>
                <TableCell>{order.priority}</TableCell>
                <TableCell>{order.assignedTo ? order.assignedTo.name : 'Unassigned'}</TableCell>
                <TableCell>{new Date(order.createdDate).toLocaleDateString()}</TableCell>
                <TableCell>{order.age}</TableCell> {/* Assuming age is already calculated */}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default WorkOrderManagement;
