import React, { useState, useEffect } from 'react';
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Grid,
  Tabs,
  Tab,
  Box,
  Typography,
  IconButton,
  Snackbar,
  Tooltip,
  Chip
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import CloseIcon from '@mui/icons-material/Close';
import useMediaQuery from '@mui/material/useMediaQuery';
import { useTheme } from '@mui/material/styles';
import axios from '../api/axiosConfig';

const statusMapping = {
  new: 'New',
  pending: 'Pending',
  delayed: 'Delayed',
  closed: 'Closed',
  excluded: 'Excluded',
  're-opened': 'Re-Opened',
};

const taskTypes = [
  'Clean Up / Spill',
  'Cooling Issue',
  'Electrical Issue',
  'Equipment Repairs',
  'Fire Safety',
  'General Maintenance Request',
  'Health and Safety',
  'Heating Issues',
  'Lighting Issues',
  'Mechanical Issues',
  'Painting / Touch Ups',
  'Pest Control',
  'Plumbing Issues',
  'Waste Issues',
];

const EditWorkOrderModal = ({ open, handleClose, workOrder, fetchWorkOrders }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const [formData, setFormData] = useState({
    status: '',
    taskType: '',
    roomNumber: '',
    description: '',
    notes: '',
    submittedBy: '',
    notesHistory: [],
    newNote: '',
    createdDate: '',
    attachments: [],
  });

  const [selectedTab, setSelectedTab] = useState(0); // Define selectedTab state
  const [tooltipOpen, setTooltipOpen] = useState(false); // Manage tooltip open state
  const [tooltipText, setTooltipText] = useState("Copy Task ID");
  const [snackbarOpen, setSnackbarOpen] = useState(false); // Define snackbar state

  useEffect(() => {
    if (workOrder) {
      const initialData = {
        _id: workOrder._id || '',
        taskType: workOrder.taskType || '',
        roomNumber: workOrder.roomNumber || '',
        description: workOrder.details || '',
        notesHistory: workOrder.notesHistory || [],
        createdDate: new Date(workOrder.createdDate).toLocaleString(),
        attachments: workOrder.attachments || [],
        submittedBy: workOrder.submittedBy
          ? `${workOrder.submittedBy.firstName} ${workOrder.submittedBy.lastName}`
          : 'N/A',
        newNote: '',
      };

      setFormData(initialData);
    }
  }, [workOrder]);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleCopyTaskId = async () => {
    if (workOrder?.workOrderId) {
      try {
        await navigator.clipboard.writeText(workOrder.workOrderId);
        setTooltipText("Copied!"); // Set tooltip to "Copied!"
        setTooltipOpen(true); // Manually open tooltip
        setTimeout(() => {
          setTooltipText("Copy Task ID"); // Revert tooltip after a delay
          setTooltipOpen(false); // Close the tooltip after the delay
        }, 1500); // Tooltip will reset after 1.5 seconds
      } catch (error) {
        setTooltipText("Failed to copy");
        setTooltipOpen(true);
        setTimeout(() => setTooltipOpen(false), 1500); // Close after showing "Failed to copy"
      }
    }
  };

  return (
    <>
      <Dialog open={open} onClose={handleClose} fullWidth maxWidth="md">
        <DialogTitle>
          Edit Work Order
          {workOrder?.workOrderId && (
            <Tooltip 
              title={tooltipText} 
              arrow 
              open={tooltipOpen} // Manually control tooltip open state
              onClose={() => setTooltipOpen(false)} // Close on outside tap/click
              disableHoverListener // Disable hover since we are controlling it manually
              placement="top"
            >
              <Chip
                label={`Task ID: ${workOrder.workOrderId}`}
                onClick={handleCopyTaskId} // Trigger tooltip and copy on click
                onTouchEnd={handleCopyTaskId} // Trigger on touch for mobile
                sx={{ float: 'right', cursor: 'pointer', fontWeight: 'bold', color: 'primary.main' }}
              />
            </Tooltip>
          )}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>Update the work order details below:</DialogContentText>

          <Tabs 
            value={selectedTab} 
            onChange={handleTabChange}
            variant="scrollable" 
            scrollButtons={false} 
          >
            <Tab label="Work Order Details" />
            <Tab label="Notes and Updates" />
            <Tab label="Attachments" />
          </Tabs>

          {selectedTab === 0 && (
            <Box>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <FormControl fullWidth margin="normal" variant="outlined">
                    <InputLabel>Status</InputLabel>
                    <Select
                      name="status"
                      value={formData.status}
                      onChange={handleChange}
                      label="Status"
                    >
                      {Object.keys(statusMapping).map((status) => (
                        <MenuItem key={status} value={status}>
                          {statusMapping[status]}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  <FormControl fullWidth margin="normal" variant="outlined">
                    <InputLabel>Task Type</InputLabel>
                    <Select
                      name="taskType"
                      value={formData.taskType}
                      onChange={handleChange}
                      label="Task Type"
                    >
                      {taskTypes.map((type) => (
                        <MenuItem key={type} value={type}>
                          {type}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  <TextField
                    label="Room Number"
                    name="roomNumber"
                    value={formData.roomNumber}
                    onChange={handleChange}
                    fullWidth
                    margin="normal"
                    variant="outlined"
                  />

                  <TextField
                    label="Submitted By"
                    name="submittedBy"
                    value={formData.submittedBy || 'N/A'}
                    disabled
                    fullWidth
                    margin="normal"
                    variant="outlined"
                  />

                  <TextField
                    label="Date Created"
                    name="createdDate"
                    value={formData.createdDate}
                    fullWidth
                    margin="normal"
                    disabled
                    variant="outlined"
                  />
                </Grid>

                <Grid item xs={12} sm={6}>
                  <TextField
                    label="Original Description"
                    name="description"
                    value={formData.description}
                    fullWidth
                    multiline
                    rows={isMobile ? 3 : 6} // Responsive rows for mobile
                    margin="normal"
                    disabled
                  />
                </Grid>
              </Grid>
            </Box>
          )}

          {selectedTab === 1 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Add new note
              </Typography>
              <TextField
                label="Add new note"
                name="newNote"
                value={formData.newNote}
                onChange={handleChange}
                fullWidth
                multiline
                rows={isMobile ? 3 : 5} // Responsive text field rows
                margin="normal"
              />
              <Button variant="contained" color="primary" onClick={handleAddNote}>
                Add Note
              </Button>

              <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
                Notes History
              </Typography>
              <Box sx={{ mt: 2 }}>
                {formData.notesHistory.map((note, index) => (
                  <Box key={index} sx={{ mb: 2, borderBottom: '1px solid #ccc', pb: 1 }}>
                    <Typography variant="body2">
                      <strong>{note.username}</strong> at{' '}
                      {new Date(note.timestamp).toLocaleString()}:
                    </Typography>
                    <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap' }}>
                      {note.note}
                    </Typography>
                    <IconButton size="small" onClick={() => handleDeleteNote(index)}>
                      <DeleteIcon fontSize="small" />
                    </IconButton>
                  </Box>
                ))}
              </Box>
            </Box>
          )}

          {selectedTab === 2 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Existing Attachments
              </Typography>
              {formData.attachments.length > 0 ? (
                <Box>
                  {formData.attachments.map((attachment, index) => (
                    <Box key={index} sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                      <Typography>
                        <button
                          onClick={() => handleDownloadAttachment(attachment)}
                          style={{ background: 'none', border: 'none', padding: 0, color: 'blue', textDecoration: 'underline', cursor: 'pointer' }}
                        >
                          {attachment.split('/').pop()}
                        </button>
                      </Typography>
                      <IconButton onClick={() => handleDeleteAttachment(attachment.split('/').pop())}>
                        <DeleteIcon />
                      </IconButton>
                    </Box>
                  ))}
                </Box>
              ) : (
                <Typography>No attachments available.</Typography>
              )}

              <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
                Upload New Attachments
              </Typography>
              <input type="file" multiple onChange={handleFileChange} />
              <Button variant="contained" color="primary" onClick={handleUploadAttachments}>
                Upload Attachments
              </Button>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleSubmit} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={7500}
        onClose={handleCloseSnackbar}
        message="Work order updated"
        action={
          <>
            <Button color="inherit" size="small" onClick={handleUndo}>
              Undo
            </Button>
            <IconButton
              size="small"
              aria-label="close"
              color="inherit"
              onClick={handleCloseSnackbar}
            >
              <CloseIcon />
            </IconButton>
          </>
        }
      />
    </>
  );
};

export default EditWorkOrderModal;
