import React, { useState, useEffect } from 'react';
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Grid,
  Tabs,
  Tab,
  Box,
  Typography,
  IconButton,
  Snackbar
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import CloseIcon from '@mui/icons-material/Close';
import axios from '../api/axiosConfig';

const EditWorkOrderModal = ({ open, handleClose, workOrder, fetchWorkOrders }) => {
  const [formData, setFormData] = useState({
    status: '',
    taskType: '',
    roomNumber: '',
    description: '',
    notes: '',
    submittedBy: '',
    notesHistory: [],
    newNote: '',
    createdDate: '',
    attachments: [],
  });

  const [selectedTab, setSelectedTab] = useState(0);
  const [selectedFiles, setSelectedFiles] = useState([]);

  useEffect(() => {
    if (workOrder) {
      const initialData = {
        _id: workOrder._id || '',
        status: workOrder.status || '',
        taskType: workOrder.taskType || '',
        roomNumber: workOrder.roomNumber || '',
        description: workOrder.details || '',
        notesHistory: workOrder.notesHistory || [],
        createdDate: new Date(workOrder.createdDate).toLocaleString(),
        attachments: workOrder.attachments || [],
        newNote: '',
      };
      setFormData(initialData);
    }
  }, [workOrder]);

  const handleFileChange = (e) => {
    setSelectedFiles(e.target.files);
  };

  const handleUploadAttachments = async () => {
    if (selectedFiles.length === 0) {
      alert('Please select at least one file.');
      return;
    }

    const formDataToUpload = new FormData();
    for (let i = 0; i < selectedFiles.length; i++) {
      formDataToUpload.append('files', selectedFiles[i]);
    }

    try {
      const response = await axios.post(`/api/work-orders/${workOrder._id}/attachments`, formDataToUpload, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setFormData({
        ...formData,
        attachments: response.data.attachments,
      });
      alert('Attachments uploaded successfully!');
    } catch (error) {
      alert('Failed to upload attachments.');
    }
  };

  const handleDeleteAttachment = async (filename) => {
    try {
      const response = await axios.delete(`/api/work-orders/${workOrder._id}/attachments/${filename}`);
      setFormData({
        ...formData,
        attachments: response.data.attachments,
      });
    } catch (error) {
      alert('Failed to delete attachment.');
    }
  };

  return (
    <Dialog open={open} onClose={handleClose} fullWidth maxWidth="md">
      <DialogTitle>Edit Work Order</DialogTitle>
      <DialogContent>
        <DialogContentText>Update the work order details below:</DialogContentText>

        <Tabs value={selectedTab} onChange={(e, val) => setSelectedTab(val)}>
          <Tab label="Work Order Details" />
          <Tab label="Notes and Updates" />
          <Tab label="Attachments" />
        </Tabs>

        {selectedTab === 2 && (
          <Box>
            <Typography variant="h6">Existing Attachments</Typography>
            {formData.attachments.length > 0 ? (
              formData.attachments.map((attachment, index) => (
                <Box key={index} sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Typography sx={{ mr: 2 }}>
                    <button
                      onClick={() => window.open(`http://localhost:5151/${attachment}`, '_blank')}
                      style={{ background: 'none', border: 'none', color: 'blue', cursor: 'pointer', textDecoration: 'underline' }}
                    >
                      {attachment.split('/').pop()}
                    </button>
                  </Typography>
                  <IconButton onClick={() => handleDeleteAttachment(attachment.split('/').pop())}>
                    <DeleteIcon />
                  </IconButton>
                </Box>
              ))
            ) : (
              <Typography>No attachments available.</Typography>
            )}

            <Typography variant="h6" sx={{ mt: 4 }}>Upload New Attachments</Typography>
            <input type="file" multiple onChange={handleFileChange} />
            <Button onClick={handleUploadAttachments} variant="contained" color="primary">Upload Attachments</Button>
          </Box>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="secondary">Cancel</Button>
        <Button onClick={handleClose} color="primary">Save</Button>
      </DialogActions>
    </Dialog>
  );
};

export default EditWorkOrderModal;
